package com.yan.asmlocalcode;import android.util.Log;import android.view.View;import java.util.WeakHashMap;/** * @author Bevan (Contact me: https://github.com/genius158) * @since 2020/9/15 */public class DoubleClickCheck {  private static WeakHashMap<View, Long> viewClickMap = new WeakHashMap<>();  public static boolean willIntercept(View v) {    Long lastClick = viewClickMap.get(v);    if (lastClick == null) lastClick = 0L;    long cur = System.currentTimeMillis();    viewClickMap.put(v, cur);    return cur - lastClick > 1000;  }}